    private static final Map<LevelName, int[]> levels = new HashMap<>();
    private static final int TILE_SIZE = 32;
    private static final int COLUMNS = 84;

    static {
        levels.put(LevelName.ONE, levelOne);
    }
    public static Level getLevel(LevelName levelName)  {
        int row = 0;
        ImageBlock leftBoundary = null;
        ImageBlock rightBoundary = null;
        List<ImageWeakBlock> imageWeakBlocks = new ArrayList<>();
        List<ImageSolidBlock> imageBlocks = new ArrayList<>();
        List<EnemyImageBlock> dynamicHorizontal = new ArrayList<>();
        int[] matrixLevel = levels.get(levelName);
        for (int i = 0; i < matrixLevel.length; i++) {
            if(i % COLUMNS == 0 ) row++;
            int positionY = row * TILE_SIZE  - padding_y;
            int positionX = (i % COLUMNS) * TILE_SIZE;
            if(levelOne[i] != 0){
                if(Arrays.binarySearch(weakBlock,levelOne[i]) <= -1){
                    ImageSolidBlock sb = new ImageSolidBlock(TILE_SIZE, TILE_SIZE, positionX, positionY, SpriteManager.world.sprites.get(matrixLevel[i] - 1));
                    imageBlocks.add(sb);
                }else{

                    if(levelOne[i] == 71){

                        EnemyImageBlock wb = null;
                        try {
                            wb = new EnemyImageBlock(positionX, positionY - (128-32));
                        } catch (IOException e) {
                            e.printStackTrace();
                        }
                        dynamicHorizontal.add(wb);
                    }else {
                        ImageWeakBlock wb = new ImageWeakBlock(TILE_SIZE, TILE_SIZE, positionX, positionY, SpriteManager.world.sprites.get(matrixLevel[i] - 1));
                        if(levelOne[i] == 5){
                            leftBoundary = wb;
                        }
                        if(levelOne[i] == 65){
                            rightBoundary = wb;
                        }
                        imageWeakBlocks.add(wb);
                    }

                }
          }
        }
        final HashMap<String, AlphabetSpriteAnimator> dialog = new HashMap<>();
        dialog.put("MAIN_BOX", new AlphabetSpriteAnimator("MAIN_BOX"," PAUSE"));
        return new Level(levelName,imageWeakBlocks, dialog, imageBlocks, dynamicHorizontal, leftBoundary,rightBoundary);
    }



